Scripts.OnClick:
local parent = this:GetParent();
local parentName = parent:GetName();
LFG.Actions.Queue.cancel(parentName, parent.event);

Scripts.OnUpdate:
this.time = this.time + arg1;
if( this.time >= 1) then
  local name = this:GetName();
  local event = this.event;
  if(event.QTE) then
    local remining = event.QTE.expireAt - time();
    if(remining > 0) then
      getglobal(name.."PendingText"):SetText("Pending - "..LFG.Utils.String.getTimeStringForPending(remining));
    end
  end
  this.time  = 0;
end

Scripts.OnClick:
local parent = this:GetParent();
local parentName = parent:GetName();
LFG.Actions.Queue.acceptQueue(parentName, parent.queueIndex);

local parent = this:GetParent();
local parentName = parent:GetName();
LFG.Actions.Queue.declineQueue(parentName, parent.queueIndex);

Scripts.OnShow:
if(not LFG.EventScrollFrames.fatchedInitially) then 
  LFG.Actions.Event.fetch();
  LFG.EventScrollFrames.fatchedInitially = true;
end
LFGEventScrollFrame:Show();

if ( not LFG.Utils.Event.validateEventInput() ) then
  this:Disable();
else
  this:Enable();
end

Scripts.OnClick:
if(this:GetText() == "Create") then
  LFG.Actions.Event.create();
else
  LFG.Actions.Event.update();
end

Scripts.OnTextChanged:
local text = this:GetText()
if(strlen(text) > 0 and this.placeHolderVisible) then
  LFGEventDescriptionPlaceholder:Hide();
  this.placeHolderVisible = false;
elseif(strlen(text) == 0 and not this.placeHolderVisible) then
  LFGEventDescriptionPlaceholder:Show();
  this.placeHolderVisible = true;
end

Scripts.OnSizeChanged:
local lines = LFG.Utils.General.explode('\n', this:GetText() or "");
if ( table.getn(lines) >= 3) then
  lines[2] = lines[2] ..lines[3];
  table.remove(lines,3);
  lines = LFG.Utils.General.implode('\n', lines);
  this:SetText(lines);
end

Scripts.OnTextChanged:
local text = this:GetText()
if(strlen(text) > 0 and this.placeHolderVisible) then
  LFGRolePickerNoteToLeaderPlaceholder:Hide();
  this.placeHolderVisible = false;
elseif(strlen(text) == 0 and not this.placeHolderVisible) then
  LFGRolePickerNoteToLeaderPlaceholder:Show();
  this.placeHolderVisible = true;
end

Scripts.OnSizeChanged:
local lines = LFG.Utils.General.explode('\n', this:GetText() or "");
if ( table.getn(lines) >= 3) then
  lines[2] = lines[2] ..lines[3];
  table.remove(lines,3);
  lines = LFG.Utils.General.implode('\n', lines);
  this:SetText(lines);
end
